
from typing import Any, Optional
from dataclasses import dataclass



#
def clamp(val: float | int, mini: int = 0, maxi: int = 255) -> int:
    if val < mini:
        return mini
    elif val > maxi:
        return maxi
    #
    return int(val)



# General purpose color class
@dataclass
class ND_Color:
    r: int = 0
    g: int = 0
    b: int = 0
    a: int = 255

    #
    def __add__(self, c: 'ND_Color') -> 'ND_Color':
        #
        nc: ND_Color = ND_Color()
        #
        nc.r = clamp(self.r+c.r, 0, 255)
        nc.g = clamp(self.g+c.g, 0, 255)
        nc.b = clamp(self.b+c.b, 0, 255)
        nc.a = clamp(self.a+c.a, 0, 255)
        #
        return nc

    #
    def __str__(self) -> str:
        return f"ND_Color({self.r}, {self.g}, {self.b}, {self.a})"

    #
    def __repr__(self) -> str:
        return f"ND_Color({self.r}, {self.g}, {self.b}, {self.a})"


#
class ND_Transformations:

    def __init__(self,
                 color_modulation: Optional[ND_Color] = None,
                 rotation: Optional[float] = None,
                 flip_x: bool = False,
                 flip_y: bool = False
        ) -> None:
        #
        self.color_modulation: Optional[ND_Color] = color_modulation
        self.rotation: Optional[float] = rotation % 360 if rotation is not None else None
        self.flip_x: bool = flip_x
        self.flip_y: bool = flip_y

    def __add__(self, t: 'ND_Transformations') -> 'ND_Transformations':
        #
        nt: ND_Transformations = ND_Transformations()
        #
        if self.color_modulation is not None:
            #
            if t.color_modulation is not None:
                #
                nt.color_modulation = self.color_modulation + t.color_modulation
            #
            else:
                nt.color_modulation = self.color_modulation
        #
        else:
            nt.color_modulation = t.color_modulation

        #
        if self.rotation is not None:
            #
            if t.rotation is not None:
                #
                nt.rotation = (self.rotation + t.rotation) % 360
            #
            else:
                nt.rotation = self.rotation
        #
        else:
            nt.rotation = t.rotation

        #
        nt.flip_x = (self.flip_x and not t.flip_x) or (t.flip_x and not self.flip_x)
        nt.flip_y = (self.flip_y and not t.flip_y) or (t.flip_y and not self.flip_y)

        #
        return nt

    #
    def __repr__(self) -> str:
        #
        return f"Transformations(color_modulation={self.color_modulation}, rotation={self.rotation}, flip_x={self.flip_x}, flip_y={self.flip_y})"


#
HEX_DIGITS: str = "0123456789abcdef"


#
def hex_to_rgba_color(hex_code: str) -> ND_Color:
    # Remove the hash symbol if present
    hex_code = hex_code.lstrip('#')

    r: int = 0
    g: int = 0
    b: int = 0
    a: int = 0

    # Convert the hex values to RGB integers
    if len(hex_code) >= 2:
        r = int(hex_code[0:2], 16)
    if len(hex_code) >= 4:
        g = int(hex_code[2:4], 16)
    if len(hex_code) >= 6:
        b = int(hex_code[4:6], 16)
    if len(hex_code) >= 8:
        a = int(hex_code[6:8], 16)

    # RGBA with default alpha = 255 (fully opaque)
    return ND_Color(r, g, b, a)


#
def color_to_int(color: ND_Color) -> int:
    #
    if hasattr(color, "a"):
        # Convert SDL_Color to 32-bit ABGR integer
        return (clamp(color.a) << 24) | (clamp(color.b) << 16) | (clamp(color.g) << 8) | clamp(color.r)
    elif hasattr(color, "r") and hasattr(color, "g") and hasattr(color, "b"):
        # Convert SDL_Color to 32-bit ABGR integer
        return (255 << 24) | (clamp(color.b) << 16) | (clamp(color.g) << 8) | clamp(color.r)
    #
    else:
        return 0


#
def cl(c: Any) -> ND_Color:

    #
    if isinstance(c, str):
        #
        c = c.lower()
        #
        if c in COLORS_DICT:
            return COLORS_DICT[c]
        #
        elif c.startswith("#") and all([cc in HEX_DIGITS for cc in c[1:]]):
            #
            return hex_to_rgba_color(c)
        #
        elif all([cc in HEX_DIGITS for cc in c[1:]]):
            #
            return hex_to_rgba_color(c)

    #
    elif isinstance(c, tuple) or isinstance(c, list):
        #
        if len(c) == 3:
            #
            if (isinstance(c[0], int) or isinstance(c[0], float)) and \
               (isinstance(c[1], int) or isinstance(c[1], float)) and \
               (isinstance(c[2], int) or isinstance(c[2], float)):
                #
                return ND_Color(int(c[0]), int(c[1]), int(c[2]), 255)
        #
        elif len(c) == 4:
            #
            if (isinstance(c[0], int) or isinstance(c[0], float)) and \
               (isinstance(c[1], int) or isinstance(c[1], float)) and \
               (isinstance(c[2], int) or isinstance(c[2], float)) and \
               (isinstance(c[3], int) or isinstance(c[3], float)):
                #
                return ND_Color(int(c[0]), int(c[1]), int(c[2]), int(c[3]))

    #
    return ND_Color(0, 0, 0, 255)  # By default, returns black



